import axios from "axios";
export const GET_POKE = "GET_POKE";
export const SEARCH_POKE = "SEARCH_POKE";
export const SORT_POKE = "SORT_POKE";
export const SORT_ATTACK = "SORT_ATTACK";
export const FILTER_TYPE = "FILTER_TYPE";
export const FILTER_CREATOR = "FILTER_CREATOR";
export const GET_DETAILS = "GET_DETAILS";

export function getPoke() {
  return function (dispatch) {
    axios
      .get("http://localhost:3001/api/pokemon")
      .then((pokemons) => {
        dispatch({
          type: "GET_POKE",
          payload: pokemons.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };
}

export function searchPoke(search) {
  return function (dispatch) {
    axios
      .get("http://localhost:3001/api/pokemon/query?name=" + search)
      .then((pokemon) => {
        dispatch({
          type: "SEARCH_POKE",
          payload: pokemon.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };
}

export function sortPoke(order) {
  return {
    type: "SORT_POKE",
    payload: order,
  };
}
export function sortAttack(payload) {
  return {
    type: "SORT_ATTACK",
    payload,
  };
}

export function filterType(payload) {
  return {
    type: "FILTER_TYPE",
    payload,
  };
}

export function filterByCreator(payload) {
  return {
    type: "FILTER_CREATOR",
    payload,
  };
}
export function getDetail(id) {
  return async function (dispatch) {
    try {
      var json = await axios.get("https://pokeapi.co/api/v2/pokemon/" + id);
      return dispatch({
        type: "GET_DETAILS",
        payload: json.data,
      });
    } catch (error) {
      console.log(error);
    }
  };
}
