import React from "react";
import s from "./Paginado.module.css";

export default function Paginado({ pokemonPerPage, pokemons, paginado }) {
  let pageNumber = [];
  for (let i = 0; i < Math.ceil(pokemons / pokemonPerPage); i++) {
    if (Math.ceil(pokemons / pokemonPerPage) <= 1) {
      pageNumber.unshift(i);
    }
    //40/12
    pageNumber.push(i + 1);
  }
  //para renderizar los numeritos
  return (
    <div className={s.container}>
      <div>
        <div className={s.center}>
          {pageNumber &&
            pageNumber.map((number) => {
              return (
                <button className={s.number} onClick={() => paginado(number)}>
                  {number}
                </button>
              );
            })}
        </div>
      </div>
    </div>
  );
}
