import React from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { searchTypes } from "../actions";
import { NavLink } from "react-router-dom";
import { postPoke } from "../actions";

export function CreatePok() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(searchTypes());
  }, []);
  const types = useSelector((state) => state.types);

  const [input, setInput] = useState({
    name: "",
    types: [],
    img: "",
    hp: "",
    attack: "",
    defense: "",
    speed: "",
    id: "",
    height: "",
    weight: "",
  });
  function handleChange(e) {
    setInput({ ...input, [e.target.name]: e.target.value });
  }
  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postPoke());
    setInput({
      name: "",
      type: [],
      img: "",
      hp: "",
      attack: "",
      defense: "",
      speed: "",
      id: "",
      height: "",
      weight: "",
    });
  }
  return (
    <div>
      <NavLink to="/pokemon">Go Back</NavLink>
      <form>
        <label>Name</label>
        <input
          type="text"
          value={input.name}
          name="name"
          onChange={handleChange}
        />
        <label>Image</label>
        <input
          type="text"
          value={input.img}
          name="img"
          onChange={handleChange}
        />
        <label>Type</label>
        <input
          type="text"
          value={input.type}
          name="type"
          onChange={handleChange}
        />
        <label>Hp</label>
        <input type="text" value={input.hp} name="hp" onChange={handleChange} />
        <label>Attack</label>
        <input
          type="text"
          value={input.attack}
          name="attack"
          onChange={handleChange}
        />
        <label>Defense</label>
        <input
          type="text"
          value={input.defense}
          name="defense"
          onChange={handleChange}
        />
        <label>Speed</label>
        <input
          type="text"
          value={input.speed}
          name="speed"
          onChange={handleChange}
        />
        <label>Id</label>
        <input type="text" value={input.id} name="id" onChange={handleChange} />
        <label>Height</label>
        <input
          type="text"
          value={input.height}
          name="height"
          onChange={handleChange}
        />
        <label>Weight</label>
        <input
          type="text"
          value={input.weight}
          name="weight"
          onChange={handleChange}
        />
        <button type="submit" onSubmit={handleSubmit}>
          Create your Pokemon!
        </button>
      </form>
    </div>
  );
}
export default CreatePok;
