import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { getPoke } from "../actions/index";
import Pokemon from "./Pokemon";
import Paginado from "./Paginado";
import { NavLink } from "react-router-dom";
import s from "../components/Pokemons.module.css";
import logo from "../img/logo.png";

export function Pokemons() {
  var pokemons = useSelector((state) => state.filteredPokemons);
  const [currentPage, setCurrentPage] = useState(1);
  const [pokemonsPerPage, setPokemonsPerPage] = useState(12);
  const indexOfLastPokemon = currentPage * pokemonsPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
  if (pokemons)
    var currentPokemons = pokemons.slice(
      indexOfFirstPokemon,
      indexOfLastPokemon
    );
  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  let dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPoke());
  }, []);

  return (
    <div>
      <ul className="ul">
        <li>
          <img src={logo} alt="" />
        </li>
        <li className="li">
          <NavLink to="/pokemons/create/poke">
            <button className={s.button}>Create</button>
          </NavLink>
        </li>
      </ul>
      <Paginado
        pokemonPerPage={pokemonsPerPage}
        pokemons={pokemons.length}
        paginado={paginado}
      />
      <div className={s.cards}>
        {currentPokemons.map((pokemon) => {
          console.log(currentPokemons);
          return (
            <div>
              <Pokemon
                id={pokemon.id}
                name={pokemon.name}
                img={pokemon.img}
                types={"Type: " + pokemon.types + " "}
                attack={pokemon.attack}
              />
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Pokemons;
