import React from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { searchTypes } from "../actions";
import { NavLink } from "react-router-dom";
import { postPoke } from "../actions";
import { useHistory } from "react-router";

export function CreatePok() {
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    dispatch(searchTypes());
  }, []);
  const types = useSelector((state) => state.types);

  const [input, setInput] = useState({
    name: "",
    types: [],
    image: "",
    hp: "",
    attack: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
  });
  function validate(input) {
    let error = {};
    if (!input.name) {
      error.name = "You have to name you pokemon!";
    }
    if (!input.types) {
      error.name = "Select at least one type for your pokemon";
    }
    if (!input.image) {
      error.name = "A picture of your pokemon is necessary";
    }
    if (!input.hp) {
      error.name = "It must be a number from 10 to 1000";
    }
    if (!input.attack) {
      error.name = "Your pokemon's attack must be a number";
    }
    if (!input.defense) {
      error.name = "Your pokemon's defense must be a number";
    }
    if (!input.speed) {
      error.name = "Your pokemon's speed must be a number";
    }
    if (!input.weight) {
      error.name = "Your pokemon's weight must be a number";
    }
    if (!input.height) {
      error.name = "Your pokemon's height must be a number";
    }
    return error;
  }
  const [error, setError] = useState();
  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    setError(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postPoke(input));
    alert("There is a new Pokemon in this world!");
    setInput({
      name: "",
      types: [],
      image: "",
      hp: "",
      attack: "",
      defense: "",
      speed: "",
      height: "",
      weight: "",
    });
    history.push("/");
  }

  function handleSelect(e) {
    setInput({
      ...input,
      types: [...input.types, e.target.value],
    });
  }

  return (
    <div>
      <NavLink to="/pokemon">Go Back</NavLink>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="name"
            value={input.name}
            name="name"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Image</label>
          <input
            type="text"
            value={input.image}
            name="img"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <select name="select" onChange={handleSelect}>
            {types && types.map((t) => <option value={t}>{t}</option>)}
          </select>
          <ul>
            <li>{input.types.map((el) => el + ",")}</li>
          </ul>
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Hp</label>
          <input
            type="number"
            value={input.hp}
            name="hp"
            min="10"
            max="1000"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Attack</label>
          <input
            type="number"
            value={input.attack}
            name="attack"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Defense</label>
          <input
            type="number"
            value={input.defense}
            name="defense"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Speed</label>
          <input
            type="number"
            value={input.speed}
            name="speed"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Height</label>
          <input
            type="number"
            value={input.height}
            name="height"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <div>
          <label>Weight</label>
          <input
            type="number"
            value={input.weight}
            name="weight"
            onChange={handleChange}
          />
          {error.name && <p>{error.name}</p>}
        </div>
        <button type="submit">Create your Pokemon!</button>
      </form>
    </div>
  );
}
export default CreatePok;
