import {
  GET_POKE,
  SEARCH_POKE,
  SORT_ATTACK,
  SORT_POKE,
  FILTER_TYPE,
  FILTER_CREATOR,
} from "../actions";
const initialState = {
  pokemons: [],
  filteredPokemons: [],
};

function rootReducer(state = initialState, action) {
  if (action.type === GET_POKE) {
    return {
      ...state,
      pokemons: action.payload,
      filteredPokemons: action.payload,
    };
  }
  if (action.type === SEARCH_POKE) {
    return {
      ...state,
      filteredPokemons: action.payload,
    };
  }
  if (action.type === SORT_POKE) {
    let orderedPokemons = [...state.pokemons];

    orderedPokemons = orderedPokemons.sort((a, b) => {
      if (a.name < b.name) {
        return action.payload === "ascendente" ? -1 : 1;
      }
      if (a.name > b.name) {
        return action.payload === "ascendente" ? 1 : -1;
      }
      return 0;
    });
    return {
      ...state,
      filteredPokemons: orderedPokemons,
    };
  }

  if (action.payload === SORT_ATTACK) {
    let orderedPokemons = [...state.pokemons];
    orderedPokemons.sort((a, b) => {
      if (action.payload === "high")
        return parseFloat(a.attack) - parseFloat(b.attack);
      if (action.payload === "low")
        return parseFloat(b.attack) - parseFloat(a.attack);
    });
    return {
      ...state,
      filteredPokemons: orderedPokemons,
    };
  }

  if (action.payload === FILTER_TYPE) {
    const pokemons = state.pokemons;
    const typeFiltered =
      action.payload === "All"
        ? pokemons
        : pokemons.filter((p) => p.types[0] === action.payload);
    // : pokemons.filter((p) => p.types === action.payload);

    return {
      ...state,
      filteredPokemons: typeFiltered,
    };
  }
  if (action.payload === FILTER_CREATOR) {
    const createdFilter =
      action.payload === "new"
        ? state.pokemons.filter((e) => e.createdInDb)
        : state.pokemons.filter((e) => !e.createdInDb);
    return {
      ...state,
      filteredPokemons:
        action.payload === "all" ? state.pokemons : createdFilter,
    };
  }

  return state;
}

export default rootReducer;
