import {
  GET_POKE,
  SEARCH_POKE,
  SORT_ATTACK,
  SORT_POKE,
  FILTER_TYPE,
} from "../actions";
const initialState = {
  pokemons: [],
  filteredPokemons: [],
};

function rootReducer(state = initialState, action) {
  if (action.type === GET_POKE) {
    return {
      ...state,
      pokemons: action.payload,
      filteredPokemons: action.payload,
    };
  }
  if (action.type === SEARCH_POKE) {
    return {
      ...state,
      filteredPokemons: action.payload,
    };
  }
  if (action.type === SORT_POKE) {
    let orderedPokemons = [...state.pokemons];

    orderedPokemons = orderedPokemons.sort((a, b) => {
      if (a.name < b.name) {
        return action.payload === "ascendente" ? -1 : 1;
      }
      if (a.name > b.name) {
        return action.payload === "ascendente" ? 1 : -1;
      }
      return 0;
    });
    return {
      ...state,
      filteredPokemons: orderedPokemons,
    };
  }

  if (action.payload === SORT_ATTACK) {
    let orderedPokemons = [...state.pokemons];
    orderedPokemons.sort((a, b) => parseFloat(a.attack) - parseFloat(b.attack));

    return {
      ...state,
      filteredPokemons: orderedPokemons,
    };
  }

  if (action.payload === FILTER_TYPE) {
    const pokemons = state.pokemons;
    const typeFiltered =
      action.payload === "All"
        ? pokemons
        : pokemons.filter((p) => p.type === action.payload);
    return {
      ...state,
      filteredPokemons: typeFiltered,
    };
  }
  return state;
}

export default rootReducer;
