import React from "react";
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions/index";
import { useEffect } from "react";

function PokeDetail(props) {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDetail(props.match.params.id));
  }, []);

  var myPoke = useSelector((state) => state.filteredPokemons);

  return (
    <div>
      {myPoke && myPoke ? (
        <div>
          <h1>Pokemon {myPoke.name}</h1>
          <h1>Type: {myPoke.types?.map((t) => t.type.name) + " "}</h1>
          <img src={myPoke.sprites?.other["official-artwork"].front_default} />
          <h1>Id: {myPoke.id}</h1>
          <h1>Height: {myPoke.height}</h1>
          <h1>Weight: {myPoke.weight} </h1>
          <h1> Hp: {myPoke.stats?.[0].base_stat}</h1>
          <h1> Attack: {myPoke.stats?.[1].base_stat} </h1>
          <h1> Defense: {myPoke.stats?.[2].base_stat} </h1>
          <h1> Speed: {myPoke.stats?.[5].base_stat}</h1>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <NavLink to="/">
        <button>Home</button>
      </NavLink>
    </div>
  );
}

export default PokeDetail;
