import React from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { searchTypes } from "../actions";
import { NavLink } from "react-router-dom";
import { postPoke } from "../actions";
import { useHistory } from "react-router";
function validate(input) {
  let errors = {};
  if (!input.name) {
    errors.name = "You have to name your Pokemon";
  } else if (!input.types) {
    errors.types = "You have to select a type";
  } else if (!input.image) {
    errors.image = "You have to add an image";
  } else if (!input.hp) {
    errors.hp = "Your pokemon's HP must be a number";
  } else if (!input.attack) {
    errors.attack = "Your pokemon's attack must be a number";
  } else if (!input.defense) {
    errors.defense = "Your pokemon's defense must de a number";
  } else if (!input.speed) {
    errors.speed = "Your pokemon's speed must be a number";
  } else if (!input.height) {
    errors.height = "Your pokemon's height must be a number";
  } else if (!input.weight) {
    errors.weight = "Your pokemon's weight must be a number";
  }
  return errors;
}
export function CreatePok() {
  const dispatch = useDispatch();
  const history = useHistory();
  const types = useSelector((state) => state.types);
  useEffect(() => {
    dispatch(searchTypes());
  }, []);
  const [errors, setErrors] = useState({});
  const [input, setInput] = useState({
    name: "",
    types: [],
    image: "",
    hp: "",
    attack: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
  });

  function handleChange(e) {
    setInput({ ...input, [e.target.name]: e.target.value });
    setErrors(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
    console.log(input);
  }
  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postPoke(input));
    console.log(input.types);
    alert("There is a new Pokemon in this world!");
    setInput({
      name: "",
      types: [],
      image: "",
      hp: "",
      attack: "",
      defense: "",
      speed: "",
      height: "",
      weight: "",
    });
    history.push("/");
  }
  function handleDelete(el) {
    console.log(el);
    setInput({
      ...input,
      types: input.types.filter((t) => t !== el),
    });
  }
  function handleSelect(e) {
    setInput({
      ...input,
      types: [...input.types, e.target.value],
    });
  }

  return (
    <div>
      <NavLink to="/pokemon">Go Back</NavLink>

      <form onSubmit={handleSubmit}>
        <div>
          <label>name</label>
          <input
            type="text"
            value={input.name}
            name="name"
            onChange={handleChange}
          />

          {errors.name && <p>{errors.name}</p>}
        </div>

        <div>
          <label>Image</label>
          <input
            type="text"
            value={input.image}
            name="image"
            onChange={handleChange}
          />
          {errors.image && <p>{errors.image}</p>}
        </div>

        <div>
          <select name="select" onChange={handleSelect}>
            {types && types.map((t) => <option value={t}>{t}</option>)}
          </select>
          {errors.types && <p>{errors.types}</p>}
        </div>

        <div>
          <label>Hp</label>
          <input
            type="number"
            value={input.hp}
            name="hp"
            min="10"
            max="1000"
            onChange={handleChange}
          />
          {errors.hp && <p>{errors.hp}</p>}
        </div>

        <div>
          <label>Attack</label>
          <input
            type="number"
            value={input.attack}
            name="attack"
            onChange={handleChange}
          />
          {errors.attack && <p>{errors.attack}</p>}
        </div>

        <div>
          <label>Defense</label>
          <input
            type="number"
            value={input.defense}
            name="defense"
            onChange={handleChange}
          />
          {errors.defense && <p>{errors.defense}</p>}
        </div>

        <div>
          <label>Speed</label>
          <input
            type="text"
            value={input.speed}
            name="speed"
            onChange={handleChange}
          />
          {errors.speed && <p>{errors.speed}</p>}
        </div>

        <div>
          <label>Height</label>
          <input
            type="text"
            value={input.height}
            name="height"
            onChange={handleChange}
          />
          {errors.height && <p>{errors.height}</p>}
        </div>

        <div>
          <label>Weight</label>
          <input
            type="text"
            value={input.weight}
            name="weight"
            onChange={handleChange}
          />
          {errors.weight && <p>{errors.weight}</p>}
        </div>

        <button type="submit">Create your Pokemon!</button>
      </form>
      {input.types.map((t) => (
        <div>
          <p>{t}</p>
          <button onClick={handleDelete}>x</button>
        </div>
      ))}
    </div>
  );
}

export default CreatePok;
