import { useDispatch } from "react-redux";
import { filterByCreator, filterType, sortAttack, sortPoke } from "../actions";

export default function Order() {
  const dispatch = useDispatch();

  function onSelectChange(e) {
    dispatch(sortPoke(e.target.value));
  }
  function onChange(e) {
    dispatch(sortAttack(e.target.value));
  }
  function handleFilter(e) {
    dispatch(filterType(e.target.value));
  }
  function onHandleSub(e) {
    dispatch(filterByCreator(e.target.value));
  }

  return (
    <div>
      <select name="select" onChange={onSelectChange}>
        <option value="ascendente">A-Z</option>
        <option value="descendente">Z-A</option>
      </select>
      <select name="select" onChange={onChange}>
        <option value="high">High-Low</option>
        <option value="low">Low-High</option>
      </select>
      <select name="select" onChange={handleFilter}>
        <option value="All">All</option>
        <option value="normal">normal</option>
        <option value="fighting">fighting</option>
        <option value="flying">flying</option>
        <option value="poison">poison</option>
        <option value="ground">ground</option>
        <option value="rock">rock</option>
        <option value="bug">bug</option>
        <option value="ghost">ghost</option>
        <option value="steel">steel</option>
        <option value="fire">fire</option>
        <option value="water">water</option>
        <option value="grass">grass</option>
        <option value="electric">electric</option>
        <option value="psychic">psychic</option>
        <option value="ice">ice</option>
        <option value="dragon">dragon</option>
        <option value="dark">dark</option>
        <option value="fairy">fairy</option>
        <option value="shadow">shadow</option>
      </select>
      <select onChange={onHandleSub}>
        <option value="all">All</option>
        <option value="new">New</option>
        <option value="old">Old</option>
      </select>
    </div>
  );
}
