import axios from "axios";
export const GET_POKE = "GET_POKE";
export const SEARCH_POKE = "SEARCH_POKE";

export function getPoke() {
  return function (dispatch) {
    axios.get("http://localhost:3001/api/pokemon").then((pokemons) => {
      dispatch({
        type: "GET_POKE",
        payload: pokemons.data,
      });
    });
  };
}

export function searchPoke(search) {
  return function (disptach) {
    axios.get("localhost:3001/api/pokemon" + search).then((pokemon) => {
      dispatch({
        type: "SEARCH_POKE",
        payload: pokemon.data,
      });
    });
  };
}
