import React from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { searchTypes } from "../actions";
import { NavLink } from "react-router-dom";
import { postPoke } from "../actions";
import { useHistory } from "react-router";

export function CreatePok() {
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    dispatch(searchTypes());
  }, []);
  const types = useSelector((state) => state.types);

  const [input, setInput] = useState({
    name: "",
    types: [],
    image: "",
    hp: "",
    attack: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
  });

  function handleChange(e) {
    setInput({ ...input, [e.target.name]: e.target.value });
  }
  function handleSubmit(e) {
    e.preventDefault();
    dispatch(postPoke(input));
    console.log(input.types);
    alert("There is a new Pokemon in this world!");
    setInput({
      name: "",
      types: [],
      image: "",
      hp: "",
      attack: "",
      defense: "",
      speed: "",
      height: "",
      weight: "",
    });
    history.push("/");
  }

  function handleSelect(e) {
    setInput({
      ...input,
      types: [...input.types, e.target.value],
    });
  }

  return (
    <div>
      <NavLink to="/pokemon">Go Back</NavLink>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            value={input.name}
            name="name"
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Image</label>
          <input
            type="text"
            value={input.image}
            name="image"
            onChange={handleChange}
          />
        </div>

        <div>
          <select name="select" onChange={handleSelect}>
            {types && types.map((t) => <option value={t}>{t}</option>)}
          </select>
          <ul>
            <li>{input.types.map((el) => el + ",")}</li>
          </ul>
        </div>

        <div>
          <label>Hp</label>
          <input
            type="number"
            value={input.hp}
            name="hp"
            min="10"
            max="1000"
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Attack</label>
          <input
            type="number"
            value={input.attack}
            name="attack"
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Defense</label>
          <input
            type="number"
            value={input.defense}
            name="defense"
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Speed</label>
          <input
            type="text"
            value={input.speed}
            name="speed"
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Height</label>
          <input
            type="text"
            value={input.height}
            name="height"
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Weight</label>
          <input
            type="text"
            value={input.weight}
            name="weight"
            onChange={handleChange}
          />
        </div>

        <button type="submit">Create your Pokemon!</button>
      </form>
    </div>
  );
}
export default CreatePok;
