import React from "react";
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions/index";
import { useEffect } from "react";

function PokeDetail(props) {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDetail(props.match.params.id));
  }, []);

  var myPoke = useSelector((state) => state.filteredPokemons);
  console.log(myPoke);
  return (
    <div>
      {myPoke.length && myPoke ? (
        <div>
          <h1>Pokemon {myPoke[0]?.name}</h1>
          <h1>Type: {myPoke[0]?.types?.map((t) => t.type.name) + " "}</h1>
          <img
            src={myPoke[0]?.sprites?.other["official-artwork"].front_default}
          />
          <h1>Id: {myPoke[0]?.id}</h1>
          <h1>Height: {myPoke[0]?.height}</h1>
          <h1>Weight: {myPoke[0]?.weight} </h1>
          <h1> Hp: {myPoke[0]?.stats?.[0].base_stat}</h1>
          <h1> Attack: {myPoke[0]?.stats?.[1].base_stat} </h1>
          <h1> Defense: {myPoke[0]?.stats?.[2].base_stat} </h1>
          <h1> Speed: {myPoke[0]?.stats?.[5].base_stat}</h1>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <NavLink to="/">
        <button>Home</button>
      </NavLink>
    </div>
  );
}

export default PokeDetail;
