import {
  GET_POKE,
  SEARCH_POKE,
  SORT_ATTACK,
  SORT_POKE,
  FILTER_TYPE,
  FILTER_CREATOR,
  GET_DETAILS,
} from "../actions";
const initialState = {
  pokemons: [],
  filteredPokemons: [],
};

function rootReducer(state = initialState, action) {
  if (action.type === GET_POKE) {
    return {
      ...state,
      pokemons: action.payload,
      filteredPokemons: action.payload,
    };
  }
  if (action.type === SEARCH_POKE) {
    return {
      ...state,
      filteredPokemons: action.payload,
    };
  }
  if (action.type === SORT_POKE) {
    let orderedPokemons = [...state.pokemons];
    orderedPokemons = orderedPokemons.sort((a, b) => {
      if (a.name < b.name) {
        return action.payload === "ascendente" ? -1 : 1;
      }
      if (a.name > b.name) {
        return action.payload === "ascendente" ? 1 : -1;
      }
      return 0;
    });
    return {
      ...state,
      filteredPokemons: orderedPokemons,
    };
  }

  if (action.payload === SORT_ATTACK) {
    let orderedPokemons = [...state.pokemons];
    console.log(action.payload);
    let filterPokes = orderedPokemons.sort((a, b) => {
      if (a.attack > b.attack) {
        return 1;
      }
      if (a.attack < b.attack) {
        return -1;
      }
      // a must be equal to b
      return 0;
    });
    console.log(filterPokes);

    return {
      ...state,
      filteredPokemons: filterPokes,
    };
  }

  if (action.payload === FILTER_TYPE) {
    const typeFiltered =
      action.payload === "All" ? <h1>chau</h1> : <h1>hola</h1>;

    // : pokemons.filter((p) => p.types === action.payload)
    return {
      ...state,
      filteredPokemons: typeFiltered,
    };
  }
  if (action.payload === FILTER_CREATOR) {
    const createdFilter =
      action.payload === "new"
        ? state.pokemons.filter((e) => e.createdInDb)
        : state.pokemons.filter((e) => !e.createdInDb);
    return {
      ...state,
      filteredPokemons:
        action.payload === "all" ? state.pokemons : createdFilter,
    };
  }
  if (action.type === GET_DETAILS) {
    return {
      ...state,
      filteredPokemons: action.payload,
    };
  }
  return state;
}

export default rootReducer;
