import React from "react";
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions/index";
import { useEffect } from "react";

function PokeDetail(props) {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDetail(props.match.params.id));
  });

  var myPoke = useSelector((state) => state.filteredPokemons);

  return (
    <div>
      {myPoke && myPoke.length > 0 ? (
        <div>
          <h1>{myPoke.data.name}</h1>

          <h1>{myPoke.data.types.map((t) => t.type)}</h1>
          <img
            src={myPoke.data.sprites.other["official-artwork"].front_default}
          />
          <h1>{myPoke.data.id}</h1>
          <h1> {myPoke.data.height}</h1>
          <h1>{myPoke.data.weight} </h1>
          <h1> {myPoke.data.stats[0].base_stat}</h1>
          <h1>{myPoke.data.stats[1].base_stat} </h1>
          <h1> {myPoke.data.stats[2].base_stat} </h1>
          <h1> {myPoke.data.stats[3].base_stat}</h1>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <NavLink to="/">
        <button>Home</button>
      </NavLink>
    </div>
  );
}

export default PokeDetail;
